package basic;

/**
 * @author DECOMM
 *
 */
public class WhileLoopDemo1 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		int sum=0;
		while(i<10) {
		sum=sum+i;
		i=i+2;
		}
	
		System.out.println("Total is "+sum);

	}


}
